var insertions = [];
var id = msg.payload.id;
var location = msg.payload.location;
var readings = msg.payload.readings;

var start_query = "INSERT INTO readings VALUES (\""+ id + "\", \"";
for (let r = 0; r < readings.length; r++) {
        const read = readings[r];
        const data = read.data;
        var mid_query = start_query + read.data_id + "\", ";
        for (let d = 0; d < data.length; d++) {
                const element = data[d];
                var end_query = mid_query
                + element.timestamp + ", "
                + element.val + ", \""
                + read.unit + "\");";
                insertions.push(end_query);
        }
}

msg = {
        id: id,
        location: location,
        readings: readings,
        payload: insertions
};

return msg;

var ids = [];
for (let index = 0; index < msg.payload.length; index++) {
    const id = msg.payload[index].device_id;
    ids.push(id);
}
msg.options = ids;
